import os.path

from google.auth.transport.requests import Request
from google.oauth2.credentials import Credentials
from google_auth_oauthlib.flow import InstalledAppFlow
from googleapiclient.discovery import build
from googleapiclient.errors import HttpError

# If modifying these scopes, delete the file token.json.
SCOPES = ['https://www.googleapis.com/auth/spreadsheets.readonly']

# The ID and range of a sample spreadsheet.
# SAMPLE_SPREADSHEET_ID = '1Xp3E6O8oLpDfAq9tTWHppjTepPWe4quVMLUaL1e3P9k'
link = input("Пане, запихніть сюди свою чудову ссилочку\n")
while link[0:8] != "https://":
    link = input("Давай ще раз. Треба ссилка типу: https://docs.google.com/spreadsheets/d/1Xp3E6O8oLpDfAq9tTWHppjTepPWe4quVMLUaL1e3P9k/edit#gid=0\n")
splitted_link = link[:link.rfind('/')][:]
SAMPLE_SPREADSHEET_ID = splitted_link[splitted_link.rfind('/')+1:]

SAMPLE_RANGE_NAME = 'Page1!A1:Z1000'

def get_the_table():
    """Shows basic usage of the Sheets API.
    Prints values from a sample spreadsheet.
    """
    creds = None
    # The file token.json stores the user's access and refresh tokens, and is
    # created automatically when the authorization flow completes for the first
    # time.
    if os.path.exists('token.json'):
        creds = Credentials.from_authorized_user_file('token.json', SCOPES)
    # If there are no (valid) credentials available, let the user log in.
    if not creds or not creds.valid:
        if creds and creds.expired and creds.refresh_token:
            creds.refresh(Request())
        else:
            flow = InstalledAppFlow.from_client_secrets_file(
                'credentials.json', SCOPES)
            creds = flow.run_local_server(port=0)
        # Save the credentials for the next run
        with open('token.json', 'w') as token:
            token.write(creds.to_json())

    try:
        service = build('sheets', 'v4', credentials=creds)

        # Call the Sheets API
        sheet = service.spreadsheets()
        result = sheet.values().get(spreadsheetId=SAMPLE_SPREADSHEET_ID,
                                    range=SAMPLE_RANGE_NAME).execute()
        values = result.get('values', [])

        if not values:
            print('No data found.')
            return

        return values

    except HttpError as err:
        print(err)


